package cdglacier.galleryfordropbox.model

import android.graphics.Bitmap
import android.graphics.BitmapFactory
import android.os.Build
import androidx.annotation.RequiresApi
import java.io.ByteArrayInputStream
import java.util.*

interface Medium {
    val id: String
    val bitmap: Bitmap
}

data class Photo(
    override val id: String,
    override val bitmap: Bitmap
) : Medium

@RequiresApi(Build.VERSION_CODES.O)
fun fakeMedia(): List<Medium> = listOf(
    "",
    "",
    "",
    ""
).map {
    val decoded = Base64.getDecoder().decode(it)
    BitmapFactory.decodeStream(ByteArrayInputStream(decoded))
}.mapIndexed { index, bitmap ->
    Photo(index.toString(), bitmap)
}
